row.process:
:INCLUDE PCK.ITPA_SHARED.PUB.INCLUDE.POWERSHELL_UTILITY :INCLUDE PCK.AUTOMIC_TOMCAT.PRV.INCLUDE.ATTACH :INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CHECK_POWERSHELL_CMD@WINDOWS :INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CHANGE_DIRECTORY_TO_AGENT_BIN
:SET &UC4RB_CONVERTED_STRING# = &UC4RB_TC_HOME_DIR# :INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CONVERT_SLASHES_WINDOWS_STYLE :SET &UC4RB_TC_HOME_DIR# = &UC4RB_CONVERTED_STRING#
!-------------------- Create Resource in server.xml -------------------------- echo "Check if server.xml exist in &UC4RB_TC_HOME_DIR#\conf" java -jar "&$AGENT_RESOURCES_CLIENT#/tomcat-tool.jar" XML ValidateDocument -xml "&UC4RB_TC_HOME_DIR#\conf\server.xml" if ( $LASTEXITCODE -gt 1 ) { $retcode = $LASTEXITCODE __uc_return( $retcode ) }
$resource = (java -jar "&$AGENT_RESOURCES_CLIENT#/tomcat-tool.jar" XML ExecutePath -xml "&UC4RB_TC_HOME_DIR#\conf\server.xml" -x '//Server/GlobalNamingResources/Resource[@name=\"&UC4RB_TC_DS_NAME#\"]' -filter all | Select-String "Return code: 0") ! Even if no Attribute found, the job does not jail if ($resource) {
echo "Resource &UC4RB_TC_DS_NAME# exists" : IF &UC4RB_TC_FAIL_IF_EXISTS# = "YES" exit 1 : ELSE exit 0 : ENDIF
}else { echo 'Adding resource:' echo '&RESOURCE_ELEMENT#'
java -jar "&$AGENT_RESOURCES_CLIENT#/tomcat-tool.jar" tomcat CreateDatasource "&UC4RB_TC_HOME_DIR#\conf\server.xml" "//Server/GlobalNamingResources" '&RESOURCE_ELEMENT#' &UC4RB_TC_DS_PWD# :INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CHECK_POWERSHELL_CMD@WINDOWS
! -------------------- Check if resource is created -------------------------- echo "Check if resource &UC4RB_TC_DS_NAME# created" $resource = (java -jar "&$AGENT_RESOURCES_CLIENT#/tomcat-tool.jar" XML ExecutePath -xml "&UC4RB_TC_HOME_DIR#\conf\server.xml" -x '//Server/GlobalNamingResources/Resource[@name=\"&UC4RB_TC_DS_NAME#\"]' -filter all | Select-String "Return code: 0")
if ("$resource") { echo "Resource &UC4RB_TC_DS_NAME# created" }else { echo "Resource &UC4RB_TC_DS_NAME# is not created. Job fail" exit 2 }
} !-------------------- Create Resource Link in context.xml -------------------------- :IF &UC4RB_TC_RES_LINKS# = "YES"
echo "Check if context.xml exist in &UC4RB_TC_HOME_DIR#/conf" java -jar "&$AGENT_RESOURCES_CLIENT#/tomcat-tool.jar" XML ValidateDocument -xml "&UC4RB_TC_HOME_DIR#\conf\context.xml" if ( $LASTEXITCODE -gt 1 ) { $retcode = $LASTEXITCODE __uc_return( $retcode ) }
$resource_link=(java -jar "&$AGENT_RESOURCES_CLIENT#/tomcat-tool.jar" XML ExecutePath -xml "&UC4RB_TC_HOME_DIR#\conf\context.xml" -x '//Context/ResourceLink[@name=\"&UC4RB_TC_DS_NAME#\"]' -filter all | Select-String "Return code: 0")
if ($resource_link) { echo "Resource Link &UC4RB_TC_DS_NAME# already exists!" exit 0 }else { echo 'Adding resource link:' echo '&RESOURCE_LINK_ELEMENT#' java -jar "&$AGENT_RESOURCES_CLIENT#/tomcat-tool.jar" XML AddElement -xml "&UC4RB_TC_HOME_DIR#\conf\context.xml" -x "//Context" -e '&RESOURCE_LINK_ELEMENT#' -filter all :INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CHECK_POWERSHELL_CMD@WINDOWS }
!-------------------- Check if resource link is created -------------------------- echo "Check if resource link &UC4RB_TC_DS_NAME# created" $resource_link=(java -jar "&$AGENT_RESOURCES_CLIENT#/tomcat-tool.jar" XML ExecutePath -xml "&UC4RB_TC_HOME_DIR#\conf\context.xml" -x '//Context/ResourceLink[@name=\"&UC4RB_TC_DS_NAME#\"]' -filter all | Select-String "Return code: 0") echo $resource_link if ($resource_link) { echo "Resource Link &UC4RB_TC_DS_NAME# created!" exit 0 }else{ echo "Resource Link &UC4RB_TC_DS_NAME# is not created. Job fail!" exit 2 }
:ELSE exit 0 :ENDIF
