row.process:
:INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CHECK_CMDLINE_CMD@WINDOWS ! Run book to query registry key
:SET &KEYS# = "" :IF &UC4RB_REG_OPTION_KEYVALUE# <> "" : SET &KEYS# = '/v "&UC4RB_REG_OPTION_KEYVALUE#"' :ENDIF :IF &UC4RB_REG_OPTION_DEFAULT# <> "" : SET &KEYS# = "/ve" :ENDIF :IF &UC4RB_REG_OPTION_ALL_SUBKEYS# <> "" : SET &KEYS# = "&KEYS# /s" :ENDIF :IF &UC4RB_REG_OPTION_USE_SEPARATOR# <> "" : SET &KEYS# = '&KEYS# /se "&UC4RB_REG_OPTION_USE_SEPARATOR#"' :ENDIF :IF &UC4RB_REG_OPTION_DATA_SEARCH# <> "" : SET &KEYS# = '&KEYS# /f "&UC4RB_REG_OPTION_DATA_SEARCH#"' : IF &UC4RB_REG_OPTION_SRCH_KEYONLY# <> "" : SET &KEYS# = "&KEYS# /k" : ENDIF : IF &UC4RB_REG_OPTION_SRCH_DATAONL# <> "" : SET &KEYS# = "&KEYS# /d" : ENDIF : IF &UC4RB_REG_OPTION_SRCH_CASESEN# <> "" : SET &KEYS# = "&KEYS# /c" : ENDIF : IF &UC4RB_REG_OPTION_SRCH_EX_MATC# <> "" : SET &KEYS# = "&KEYS# /e" : ENDIF :ENDIF :IF &UC4RB_REG_OPTION_SRCH_TYPE# <> "" : SET &KEYS# = '&KEYS# /t "&UC4RB_REG_OPTION_SRCH_TYPE#"' :ENDIF :IF &UC4RB_REG_OPTION_VERBOSE# <> "" : SET &KEYS# = "&KEYS# /z" :ENDIF
@echo off ! Query specified registry key %SYSTEMROOT%\system32\reg.exe QUERY "&UC4RB_REG_KEY#" &KEYS# set returncode=%errorlevel% if %returncode% EQU 0 ( echo Successfully queried key &UC4RB_REG_KEY# ) else ( echo Error occurred whilst querying key &UC4RB_REG_KEY#. ) exit /b %returncode%
